import React, { createRef, useEffect } from "react";
import Menu from "./MenuItem";
import "./ProductGrid.css";
import image from "../tea.png";
import SizePrice from "./SizePrice";
import { motion, useViewportScroll, useTransform } from "framer-motion";
import LocomotiveScroll from "locomotive-scroll";

//Ease
const transition = { duration: 4, ease: [0.6, 0.01, 0.05, 0.9] };

const ProductGrid = ({ productName, backdgroundColor, textColor }) => {
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [80, 160]);
  const scrollRef = createRef();
  useEffect(() => {
    const scrolls = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
    });
  });
  return (
    <motion.div
      whileHover="hover"
      transition={transition}
      className="productGrid"
      ref={scrollRef}
    >
      <div className={`item item-wrapper`} data-scroll-section>
        <a>
          <div className="border" data-scroll></div>
          <div className={`parallax red`} data-scroll>
            <div className="centering" data-scroll>
              <motion.img
                style={{ translateY: scale }}
                initial={{ scale: 0.8 }}
                animate={{
                  transition: { delay: 0.1, ...transition },
                }}
                src={
                  "https://raw.githubusercontent.com/cnhhoang850/BoomCoffee/gh-pages/static/media/tea.9c74c900.png"
                }
                id="image"
                data-scroll
              />
            </div>
          </div>
        </a>
      </div>
      <SizePrice data-scroll-section />
      <div className={`about order-wrapper`} data-scroll-section>
        <a href="">
          <div className={`textParallax`}>
            <div className="textBox">
              <div className="title">
                <p className="textHeader">
                  {"Topping "}
                  <br />
                </p>
                <span className="textPrice">{"29k"}</span>
              </div>
            </div>
          </div>
        </a>
      </div>
      <div className={`options order-wrapper`} data-scroll-section>
        <a href="">
          <div className={`textParallax`}>
            <div className="textBox">
              <div className="title">
                <p className="textHeader">
                  {"Options "}
                  <br />
                </p>
                <span className="textPrice">{"29k"}</span>
              </div>
            </div>
          </div>
        </a>
      </div>
    </motion.div>
  );
};

export default ProductGrid;
