import Navigation from "./components/Navigation";
import Divider from "./components/Divider";
import Menu from "./components/Menu";
import Search from "./components/SearchBar";
import Product from "./components/ProductGrid/Product_Grid";
import Footer from "./components/Footer";
import "./App.css";
import { AnimatePresence } from "framer-motion";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import LocomotiveScroll from "locomotive-scroll";
import React, { useffect } from "react";

let menuItems = [
  {
    firstName: "FRAPPU",
    lastName: "CHOCO",
    price: "29.000VND",
    index: 1,
  },
  {
    firstName: "FRAPPU",
    lastName: "VANI",
    price: "39.000VND",
    index: 2,
  },
  {
    firstName: "COFFEE",
    lastName: "CHOCO",
    price: "29.000VND",
    index: 3,
  },
  {
    firstName: "FRAPPU",
    lastName: "MINT",
    price: "59.000VND",
    index: 4,
  },
  {
    firstName: "MARU",
    lastName: "MATCHA",
    price: "69.000VND",
    index: 5,
  },
  {
    firstName: "FRAPPU",
    lastName: "RED",
    price: "29.000VND",
    index: 6,
  },
  {
    firstName: "COFFEE",
    lastName: "MALLOW",
    price: "79.000VND",
    index: 7,
  },
  {
    firstName: "MILK",
    lastName: "BOBA",
    price: "29.000VND",
    index: 8,
  },
  {
    firstName: "MILK",
    lastName: "TEA",
    price: "29.000VND",
    index: 9,
  },
  {
    firstName: "PEACH",
    lastName: "TEA",
    price: "29.000VND",
    index: 10,
  },
];

function App() {
  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: document.getElementById("main-wrapper"),
      smooth: true,
      lerp: 0.1,
    });
  });
  return (
    <div id="main-wrapper">
      <Navigation />
      <Divider />
      <Search data-scroll />
      <Route
        render={({ location }) => (
          <AnimatePresence initial={false} exitBeforeEnter>
            <TransitionGroup component={null}>
              <CSSTransition key={location.key} in={true} classNames="slide-in">
                <Switch location={location} key={location.pathname}>
                  <Route
                    exact
                    path="/"
                    render={() => <Menu menuItems={menuItems} data-scroll />}
                  />
                  <Route
                    exact
                    path="/product"
                    render={() => <Product data-scroll />}
                  />
                </Switch>
              </CSSTransition>
            </TransitionGroup>
          </AnimatePresence>
        )}
      />
      <Footer />
    </div>
  );
}

export default App;
