import Navigation from "./components/Navigation";
import Divider from "./components/Divider";
import Menu from "./components/Menu";
import Search from "./components/SearchBar";
import Product from "./components/Product_Grid";
import "./App.css";
import { AnimatePresence } from "framer-motion";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import LocomotiveScroll from "locomotive-scroll";
import React, { createRef, useEffect } from "react";

function App() {
  const scrollRef = createRef();
  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      smoothMobile: true,
    });
  });
  let menuItems = [
    {
      firstName: "FRAPPU",
      lastName: "CHOCO",
      price: "29.000VND",
      index: 1,
    },
    {
      firstName: "FRAPPU",
      lastName: "VANI",
      price: "39.000VND",
      index: 2,
    },
    {
      firstName: "COFFEE",
      lastName: "CHOCO",
      price: "29.000VND",
      index: 3,
    },
    {
      firstName: "FRAPPU",
      lastName: "MINT",
      price: "59.000VND",
      index: 4,
    },
    {
      firstName: "MARU",
      lastName: "MATCHA",
      price: "69.000VND",
      index: 5,
    },
    {
      firstName: "FRAPPU",
      lastName: "RED",
      price: "29.000VND",
      index: 6,
    },
    {
      firstName: "COFFEE",
      lastName: "MALLOW",
      price: "79.000VND",
      index: 7,
    },
    {
      firstName: "MILK",
      lastName: "BOBA",
      price: "29.000VND",
      index: 8,
    },
    {
      firstName: "MILK",
      lastName: "TEA",
      price: "29.000VND",
      index: 9,
    },
    {
      firstName: "PEACH",
      lastName: "TEA",
      price: "29.000VND",
      index: 10,
    },
  ];
  return (
    <Router>
      <div ref={scrollRef}>
        <Navigation />
        <Divider />
        <Search data-scroll-section />
        <Route
          render={({ location }) => (
            <AnimatePresence initial={false} exitBeforeEnter>
              <Switch location={location} key={location.pathname}>
                <Route
                  exact
                  path="/"
                  render={() => <Menu menuItems={menuItems} data-scroll />}
                />
                <Route
                  exact
                  path="/product"
                  render={() => <Product data-scroll />}
                />
              </Switch>
            </AnimatePresence>
          )}
        />
      </div>
    </Router>
  );
}

export default App;
