import React from "react";
import image from "../tea.png";
import { Link } from "react-router-dom";
import { motion, useViewportScroll, useTransform } from "framer-motion";

const MenuItem = ({ firstName, lastName, price, imageUrl, index }) => {
  //index + 1 because array is 0 index

  const colors = ["orange", "blue", "red", "green", "milk"];

  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 0.8], [60, 280]);
  const findColor = (accentName) => {
    let color;
    switch (accentName) {
      case "FRAPPU":
        color = "red";
        break;
      case "MARU":
        color = "orange";
        break;
      case "COFFEE":
        color = "blue";
        break;
      case "MILK":
        color = "milk";
        break;
      default:
        color = "red";
    }

    return color;
  };
  return (
    <div className={`item-wrapper item-${index}`}>
      <Link to="/product">
        <div className="border"></div>
        <div className={`parallax ${findColor(firstName)}`}>
          <div className="centering">
            <div className="itemTitle">
              <p className="itemHeader">
                {firstName}
                <br />
                {lastName}
              </p>
              <span className="itemPrice">{price}</span>
            </div>
            <motion.img
              src={image}
              style={{ translateY: scale }}
              src={image}
              className="img"
            />
          </div>
        </div>
      </Link>
    </div>
  );
};

export default MenuItem;
